% Close ALL figure windows.close all hidden forceif exist('PsychtoolboxVersion','file')    Screen('CloseAll');    scrns=Screen('screens');    for i=1:numel(scrns)        newGammaTab=repmat((0:1/WhiteIndex(scrns(i)):1)',1,3).^1;        Screen('LoadNormalizedGammaTable',scrns(i),newGammaTab);    endend%disp 'All figures closed.'